sect1.programlisting:
public List<Employee> searchByName(@Named("Employee Name")final String name) {
        Filter filter = new Filter() {
            public boolean accept(Object pojo) {
                return ((Employee) pojo).getName().toLowerCase().indexOf(name.toLowerCase()) >= 0;
            }
        };
        List<Employee> results = allMatches(Employee.class, filter, false);
        if (results.size() > 20) {
            throw new ApplicationException("More than 20 matches  -  please narrow search criteria");
        }
        return results;
}

public List<Employee> searchByName(String name) {
        Query query = createEntityQuery("lower(o.name) like ?", Employee.class).setString(0, name + "%").setMaxResults(20);
        List list = Arrays.asList(findByQuery(query, Employee.class));
        return new ArrayList<Employee>(list);
}

